mapper.insert:
INSERT INTO AccountBook(
                         member_id
                       , transaction_date
                       , category
                       , amount
                       , type
                       , description
                       , payment_method
                       , created_at
                        )
VALUES (
        #{memberId}
      , #{transactionDate}
      , #{category}
      , #{amount}
      , #{type}
      , #{description}
      , #{paymentMethod}
      , now()
       )

    SELECT LAST_INSERT_ID()

mapper.select:
SELECT SUM(amount)
FROM AccountBook
WHERE category = #{category}
  AND transaction_date BETWEEN #{startDate} AND #{endDate}

SELECT SUM(a.amount) * (1 - (MAX(g.target_percent) / 100)) AS adjusted_amount
FROM AccountBook a
         JOIN Goal g ON a.category = g.goal_Category
WHERE a.category = #{categoryId}
  AND a.transaction_date BETWEEN DATE_SUB(CURRENT_DATE(), INTERVAL 1 MONTH) AND LAST_DAY(DATE_SUB(CURRENT_DATE(), INTERVAL 1 MONTH))
GROUP BY a.category;
